    private void crearProducto() {
        String cajaReferenciaProducto = cajaReferencia.getText();
        String cajaNombreProducto = cajaNombre.getText();
        Double cajaPreciocostoProducto = Double.parseDouble(cajaPreciocosto.getText());
        Double cajaPrecioventaProducto = Double.parseDouble(cajaPrecioventa.getText());

        if (cajaReferenciaProducto.isEmpty()) {
            JOptionPane.showMessageDialog(panelCuerpoCrearP, "Debe escribir la referencia del producto.");
        } else if (cajaNombreProducto.isEmpty()) {
            JOptionPane.showMessageDialog(panelCuerpoCrearP, "Debe escribir el nombre del producto.");
        } else if (cajaPreciocostoProducto.isNaN()) {
            JOptionPane.showMessageDialog(panelCuerpoCrearP, "Debe escribir el precio de costo del producto.");
        } else if (cajaPrecioventaProducto.isNaN()) {
            JOptionPane.showMessageDialog(panelCuerpoCrearP, "Debe escribir el precio de venta del producto.");
        }
        
        else {
            DAOProducto objDao = new DAOProducto();
            Producto objProducto = new Producto();
            
            objProducto.setReferencia(cajaReferenciaProducto);
            objProducto.setNombre(cajaNombreProducto);
            objProducto.setPrecioCosto(cajaPreciocostoProducto);
            objProducto.setPrecioVenta(cajaPrecioventaProducto);
            
            if(objDao.agregar(objProducto)){
                cajaReferencia.setText("");
                cajaNombre.setText("");
                cajaPreciocosto.setText("");
                cajaPrecioventa.setText("");
                JOptionPane.showMessageDialog(panelCuerpoCrearP, "Registro agregado correctamente.");
            }else{
                JOptionPane.showMessageDialog(panelCuerpoCrearP, "No se puede crear el registro.");
            }
            cajaReferencia.requestFocus();
        }
    }

    private void btnCrearProductoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCrearProductoActionPerformed
        crearProducto();
    }//GEN-LAST:event_btnCrearProductoActionPerformed

